import React from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import axios from 'axios';

class NewStatus extends React.Component{
    constructor(props){
        super(props);
        this.state={            
            personalData:[],
            status:false
        }       
        
        this.handleStatus=this.handleStatus.bind(this);
        this.handleUpdate=this.handleUpdate.bind(this);
        this.handleChange=this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({
          status: e.target.value
        });
      }

    handleStatus(e){
        e.preventDefault();
        document.getElementById("statusshow").style.display="none";
        document.getElementById("statusedit").style.display="block";
    }

    handleUpdate(e){
        e.preventDefault();
        if(this.state.status=="1"){
            let statusReq = {
                "status":1
            }
            axios.put("http://localhost:3000/worker/status/"+localStorage.getItem("UserId"), statusReq)
            document.getElementById("statusshow").style.display="block";
            document.getElementById("statusedit").style.display="none";
        }else{
            let statusReq = {
                "status":0
            }
            axios.put("http://localhost:3000/worker/status/"+localStorage.getItem("UserId"), statusReq)
            document.getElementById("statusshow").style.display="block";
            document.getElementById("statusedit").style.display="none";
        }
    }
    componentDidMount(){
        let userId = localStorage.getItem('UserId');
        let url = "http://localhost:3000/worker/profile/"+localStorage.getItem("UserId");    
        axios.get(url,{withCredentials:true})
        .then(response => {             
            this.setState({personalData:response.data.result.recordsets[0][0]})
           
        })
        .catch(error => {
            console.log(error)
        })
     }
    render(){
        return (
            <MDBCol>
              <MDBCard style={{ width: "100%" }}>
                <MDBCardImage style={{marginLeft:"30%"}}className="img-fluid" src="data:image/png;base64," waves />
                <MDBCardBody>
                  <MDBCardTitle>{this.state.personalData.UserEmail}</MDBCardTitle>
                  <MDBCardText>
                            <div class="row" >
                                <div class="col-md-5">Email</div>
                                <div class="col-md-5"><input type="text" name="email" value={this.state.personalData.UserEmail} disabled></input></div>
                            </div>
                            <br></br>

                            <div id="statusshow" style={{ display: "block" }}>
                                {/* <div class="row" >
                                    <div class="col-md-5"><input type="radio" name="status" disabled  />Online</div>
                                    <div class="col-md-5"><input type="radio" name="status" disabled />OffLine</div>
                                </div> */}
                                <MDBBtn onClick={this.handleStatus}>Update Status</MDBBtn>
                            </div>

                            <div id="statusedit" style={{ display: "none" }}>
                                <div class="row" >
                                    <div class="col-md-5"><input type="radio" name="status" value="1" checked={this.state.status==="1"} onChange={this.handleChange}/>Online</div>
                                    <div class="col-md-5"><input type="radio" name="status" value="0" checked={this.state.status==="0"} onChange={this.handleChange}/>OffLine</div>
                                </div>
                                <MDBBtn  onClick={this.handleUpdate} >Update</MDBBtn>
                            </div>
                  </MDBCardText>
               
                </MDBCardBody>
              </MDBCard>
            </MDBCol>
          )
    }
}
export default NewStatus;